var five = require("../lib/johnny-five");
var board = new five.Board();

board.on("ready", function() {
  var l = new five.LCD({
    controller: "PCF8574T",
    rows: 4,
    cols: 20
  });

  l.useChar("heart");
  l.cursor(0, 0).print("hello :heart:");
  l.blink();
  l.cursor(1, 0).print("Blinking? ");
  l.cursor(2, 0).print("3rd row");
  l.cursor(3, 0).print("4th row");
});
